function Task({ id, content, completed, onToggle }) {
  return (
    <div>
      <input
        type="checkbox"
        checked={completed}
        onChange={() => onToggle(id)}
      />
      <span style={{ textDecoration: completed ? 'line-through' : 'none' }}>
        {content}
      </span>
    </div>
  );
}

export default Task;
